#include "softfloat-for-gcc.h"
#include "milieu.h"
#include "softfloat.h"

float32 __negsf2(float32);

float32
__negsf2(float32 a)
{

	/* libgcc1.c says INTIFY(-a) */
	return a ^ 0x80000000;
}
